mapper.select:
SELECT id, trade_type tradeType, business_code businessCode, trade_sources tradeSources, contract_num contractNum, batch, batch_foiu_finance batchFoiuFinance, trading_time tradingTime, transaction_amount transactionAmount, user_account userAccount, user_name userName, in_account inAccount, in_user_name inUserName, total_money totalMoney, balance, remark, return_num returnNum, state, account_state accountState, boolean_type booleanType FROM t_gq_fss_fuiou_account_info t WHERE (boolean_type != '-1' or boolean_type is null)
AND t.trade_type = #{tradeType}
AND business_code = #{businessCode}
AND user_name = #{userName}
AND date_format(trading_time,'%Y-%m-%d') = #{tradingTime}
ORDER BY t.trading_time

SELECT id, trade_type tradeType, business_code businessCode, trade_sources tradeSources, contract_num contractNum, batch, batch_foiu_finance batchFoiuFinance, trading_time tradingTime, transaction_amount transactionAmount, user_account userAccount, user_name userName, in_account inAccount, in_user_name inUserName, total_money totalMoney, balance, remark, return_num returnNum, state, account_state accountState, boolean_type booleanType FROM t_gq_fss_fuiou_account_info t WHERE boolean_type = '-1'
AND t.id = cast(#{id} as signed int)
AND t.trade_type = #{tradeType}
AND date_format(t.trading_time,'%Y-%m-%d') = #{tradingTime}
ORDER BY t.trading_time
