mapper.select:



			AND t.apply_no like CONCAT('%',#{applyNo},'%')


			AND t.busi_no like CONCAT('%',#{busiNo},'%')


			AND t.`result_state` = #{resultState}


			and date_format(t.create_time,'%Y%m%d') >= #{startTime}


			and date_format(t.create_time,'%Y%m%d') <= #{endTime}


order by t.create_time desc

SELECT t.`id` id, t.`seq_no` seqNo, t.`chg_cd` chgCd, t.`chg_dt` chgDt, t.`amt` amt, CASE WHEN t.`result_state`='10120002' THEN '充值码获取成功，待客户充值' WHEN t.`result_state`='10120003' THEN '充值成功' WHEN t.`result_state`='10120004' THEN '充值码获取失败' ELSE t.`result_state` END payState FROM `t_gq_fss_offline_recharge` t WHERE t.`cust_id`=#{custId} AND t.`cust_type`=#{custType}
AND t.chg_dt >= #{startTime}
AND t.chg_dt <= #{endTime}
ORDER BY t.`id` DESC
