mapper.select:

	WHERE 1=1

		AND t.apply_type=#{applyType}


		AND t.busi_type=#{busiType}


		AND t.apply_no=#{applyNo}


		AND t.business_no=#{businessNo}


		AND c.`name`=#{custName}


		AND c.mobile=#{custMobile}


		AND t.apply_state=#{applyState}


		AND t.trade_state=#{tradeState}


		and date_format(t.create_time,'%Y%m%d') >= #{startTime}


		and date_format(t.create_time,'%Y%m%d') <= #{endTime}


		and t.id = #{id}

order by t.create_time desc
