mapper.select:

where t.order_type in(1,2)

	and t.account_id in

		#{accNo}



	AND t.`order_no` like CONCAT('%',#{map.orderNo},'%')


	and date_format(t.create_time,'%Y%m%d') >= #{map.startTime}


	and date_format(t.create_time,'%Y%m%d') <= #{map.endTime}

ORDER BY  t.create_time DESC


where t.abnormal_state=98080002

	AND t.`order_no` like CONCAT('%',#{map.orderNo},'%')


	and date_format(t.create_time,'%Y%m%d') >= #{map.startTime}


	and date_format(t.create_time,'%Y%m%d') <= #{map.endTime}

ORDER BY  t.create_time DESC
