mapper.select:

where 1=1

	and t.contract_no like concat('%',#{contractNo},'%')


	and t.status like concat('%',#{status},'%')


	and t.mchn_child like concat('%',#{mchnChild},'%')


	and date_format(t.create_time,'%Y%m%d') >= #{startTime}


	and date_format(t.create_time,'%Y%m%d') <= #{endTime}


	AND t.trade_type = #{type}
