mapper.select:
SELECT id id, parent_id parentId, acc_no accNo, trade_type tradeType, create_time createTime, motify_time motifyTime, amt, state, result_state resultState, seq_no seqNo, serial_number serialNumber, contract_id contractId, mchn_parent mchnParent, mchn_child mchnChild, remark, resp_code respCode, resp_msg respMsg FROM t_gq_fss_repayment
and parent_id = #{parentId}
and acc_no like CONCAT('%',#{accNo},'%')
and contract_id like CONCAT('%',#{contractId},'%')
and serial_number like CONCAT('%',#{serialNumber},'%')
and state = #{state}
and result_state = #{resultState}
